mapper.select:

select SQL_CALC_FOUND_ROWS * from(select a.subjectid,a.type,a.content,a.option1,a.option2,a.option3,a.option4,a.answer,a.difficulty,a.chaptername,course.coursename,a.chapterid,a.courseid from (select subjectid,type,content,option1,option2,option3,option4,answer,difficulty,chaptername,courseid,chapter.chapterid from question
    left join chapter  on question.chapterid=chapter.chapterid )a left join course on course.courseid=a.courseid)a
limit #{beginIndex},#{pageSize};select found_rows() count;


select SQL_CALC_FOUND_ROWS * from(select a.subjectid,a.type,a.content,a.option1,a.option2,a.option3,a.option4,a.answer,a.difficulty,a.chaptername,course.coursename,a.courseid,a.chapterid from
 (select subjectid,type,content,option1,option2,option3,option4,answer,difficulty,chaptername,courseid,chapterid from question left join chapter  on question.chapterid=chapter.chapterid
 where question.difficulty=#{difficulty,jdbcType=VARCHAR})a left join course on course.courseid=a.courseid)a limit #{beginIndex,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER};select found_rows() count;


select SQL_CALC_FOUND_ROWS * from(select a.subjectid,a.type,a.content,a.option1,a.option2,a.option3,a.option4,a.answer,a.difficulty,a.chaptername,course.coursename,a.courseid,a.chapterid from
    (select subjectid,type,content,option1,option2,option3,option4,answer,difficulty,chaptername,courseid,chapterid from question
        left join chapter  on question.chapterid=chapter.chapterid where question.content like CONCAT('%',#{question,jdbcType=VARCHAR},'%'))a left join course on course.courseid=a.courseid)a limit #{beginIndex,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER};select found_rows() count;
